package com.example.recycleviewproject;

import androidx.appcompat.app.AppCompatActivity;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import android.os.Bundle;
import android.view.View;
import android.widget.Toast;

import java.util.ArrayList;

public class MainActivity extends AppCompatActivity implements AdapterMakanan.ItemClickListener{

    ArrayList<ModelMakanan> modelMakanans = new ArrayList<>();

    String[] imageMakanans = {
            "data:image/jpeg;base64,",
            "data:image/jpeg;base64,",
            "data:image/jpeg;base64,",
            "data:image/jpeg;base64,",
            "https://www.astronauts.id/blog/wp-content/uploads/2023/10/Resep-Mie-Aceh-Goreng-Simpel-Untuk-Masak-di-Rumah.jpg",
            "data:image/jpeg;base64,",
            "data:image/jpeg;base64,",
            "data:image/jpeg;base64,"
    };
    AdapterMakanan adpter;


    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);

        RecyclerView recyclerView = findViewById(R.id.recycleview);

        setModelMakanans();

        adpter = new AdapterMakanan(this,modelMakanans);
        recyclerView.setAdapter(adpter);
        recyclerView.setLayoutManager(new LinearLayoutManager(this));

        adpter.setClickListener(this);
    }

    private void setModelMakanans(){
        String[] makananName = getResources().getStringArray(R.array.nama_makanan);
        String[] makananDes = getResources().getStringArray(R.array.deskripsi_makanan);

        for(int i = 0;i<makananName.length;i++){
            modelMakanans.add(new ModelMakanan(makananName[i],makananDes[i],imageMakanans[i]));
        }
    }
    public void onItemClick(View view, int position) {
        Toast.makeText(this, "You clicked " + adpter.getItem(position).getName() + " on row number " + position, Toast.LENGTH_SHORT).show();
    }
}